package codoid.technicaltask.java;

public class PrintStarWithEqualRows {

	public static void main(String[] args) {
		  int rows = 5; // number of rows
	        for(int i = 1; i <= rows; i++) {
	        	for(int j=1;j<=rows;j++) {
	        		System.out.print("*");
	        	}
	            System.out.println();
	        }
		// TODO Auto-generated method stub

	}

}
